import React from "react";

class Password extends Comment {
  /* function handleChange(e) {
        const checked = e.target.value;
        console.log(checked);
    };*/

  state = {
    symbol: null,
  };

  handleChange = event => {
    this.setState({
      symbol: event.target.files[0],
    });
  };

  render() {
    return (
      <div>
        <h1>My password generator</h1>
        <div>
          <input
            type="checkbox"
            id="symbols"
            name="symbols"
            value="symbols"
            onChange={this.handleChange}
          />
          Symbols (&, !, ? ...)
        </div>
        <div>
          <input type="checkbox" id="capital" name="capital" value="capital" />
          Capital letter (A, C, D, ...)
        </div>
        <div>
          <input type="checkbox" id="small" name="small" value="small" />
          Small letter (a, c, d ...)
        </div>
        <div>
          <div>password lengh :</div>
          <div> choose beeween 1 and 12 characters </div>
          <input type="range" min={1} max={12} />
        </div>
        <button className="btn btn-alerte" onClick={handleChange}>
          GENERATE
        </button>
      </div>
    );
  }
}

export default Password;
